mapper.select:
select picture_id, picture_title, picture_path from picture
where association_id = #{associationId}
order by picture_id desc

select picture_id, picture_title, picture_path, association_id, picture_category, create_time, user_id, is_icon
from picture
where picture_id = #{pictureId}

mapper.update:
update picture
set is_icon = #{isIcon}
where picture_id = #{pictureId}

mapper.delete:
delete from picture where picture_id in
#{pictureId}
and is_icon = 0
