mapper.select:
select rights_id, association_name, rights_title, rights_status, solution
from rights r
join association a on r.association_id = a.association_id
where rights_status = #{status}

    and r.association_id = #{associationId}

select  rights_id, association_name, student_name, phone, email, rights_title, rights_content,
        rights_status, solution, r.create_time, r.update_time
from rights
join association a on r.association_id = a.association_id
where rights_id = #{rightsId}

    and r.association_id = #{associationId}

mapper.update:
update rights
set rights_status = #{status}

    , solution = #{solution}

where rights_id = #{rightsId}

    and association_id = #{associationId}
