mapper.select:
select video_id, video_title, video_path
from video
order by create_time desc limit #{count}

select video_id, video_title, video_description, video_path, create_time, user_name from video v join user u on u.user_id = v.user_id
where u.association_id = #{associationId}
order by create_time desc

mapper.update:
update video
set video_title = #{videoTitle},
    video_description = #{videoDescription}
where video_id = #{videoId}
